mapper.select:

SELECT *
FROM(	SELECT @RN:=@RN+1 AS rnum, a.*
	FROM(
		SELECT *
		FROM bbs_t
		WHERE status = 0 AND bname = #{bname}
		ORDER BY b_idx DESC
	) AS a, (SELECT @RN:=0) AS b) as c
WHERE c.rnum BETWEEN #{begin} AND #{end};

SELECT COUNT(*)
FROM bbs_t
WHERE status = 0 AND bname = #{bname}

mapper.insert:
INSERT INTO bbs_t(subject, writer, content, file_name,
ori_name, write_date, ip, hit, bname,  status)
VALUES(#{subject}, #{writer}, #{content}, #{file_name}, #{ori_name}, NOW(), #{ip}, 0, #{bname} , 0);

mapper.update:
UPDATE bbs_t
SET hit = hit+1
WHERE b_idx = #{b_idx}

UPDATE bbs_t

	subject = #{subject},
	content = #{content},

		file_name = #{file_name},
		ori_name = #{ori_name}


WHERE b_idx = #{b_idx}

UPDATE bbs_t
SET status = 1
WHERE b_idx = #{b_idx}
